import React, {Component} from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams,
    useRouteMatch
} from "react-router-dom";
import './App.css';
import axios from "axios";
import { Redirect } from 'react-router-dom'
import Cookies from 'universal-cookie';
import {Welcome} from './welcome'

class App extends Component {

    render() {
        return (
            <div className="App">
                <Router>
                    <div>
                        <ul>
                            <li>
                                <Link to="/">Home</Link>
                            </li>
                            <li>
                                <Link to="/welcome">Channel général</Link>
                            </li>
                        </ul>

                        <hr />

                        <Switch>
                            <Route exact path="/">
                                <Home />
                            </Route>
                            <Route path="/welcome">

                            </Route>
                        </Switch>
                    </div>
                </Router>
            </div>
        );
    }
}

class Home extends Component{
    constructor(props) {
        super(props);
        this.state = {
            isSignedUp: false,
            name: '',
            timestamp: 'no timestamp yet',
        }

        Welcome((err, timestamp) => this.setState({
            timestamp
        }));
    }

    cookies = new Cookies();

    mySubmitHandler = (event) => {
        event.preventDefault()
        this.cookies.set('username', this.state.name)
    }

    myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
    }

    render() {
        return (
            <div>
                <p className="App-intro">
                    This is the timer value: {this.state.timestamp}
                </p>
                <h2>Home</h2>

                <p>Mon pseudo: {this.cookies.get('username')}</p>

                <form onSubmit={this.mySubmitHandler} method='POST' action='/login'>
                    <input name="name" type="text" placeholder="Name" onChange={this.myChangeHandler} value={this.state.name} />
                    <input name="submit" type="submit" value="Valider mon pseudo" onChange={this.myChangeHandler} />
                </form>
            </div>
        )
    }
}

export default App;
