import React, {Component} from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams,
    useRouteMatch
} from "react-router-dom";
import './App.css';
import axios from "axios";
import moment from "moment";
import ReactDOM from "react-dom";
import { Redirect } from 'react-router-dom'
import Cookies from 'universal-cookie';
import {Welcome} from './welcome'
import socketIOClient from "socket.io-client";
const {useEffect} = require("react");
const {useState} = require("react");

const username = prompt("Quel est votre pseudo ?");
moment.locale('fr')

const socket = socketIOClient("http://localhost:4242", {
    transports: ["websocket", "polling"]
});

const App = ({}) => {
    const [users, setUsers] = useState([]);
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        socket.on("connect", () => {
            socket.emit("username", username);
        });

        socket.on("users", users => {
            setUsers(users);
        });

        socket.on("message", message => {
            setMessages(messages => [...messages, message]);
        });

        socket.on("connected", user => {
            setUsers(users => [...users, user]);
        });
    }, []);

    const submit = event => {
        event.preventDefault();
        socket.emit("send", message);
        setMessage("");
    };

    return (
        <div>
            <h6>Bonjour {username}</h6>
            <div className="row">
                <div className="col-md-8">
                    <h6>Messages</h6>
                    <div id="messages">
                        {messages.map(({ user, date, text }, index) => (
                            <div key={index} className="row mb-2">
                                <div className="col-md-3">
                                    {moment(date).format("hh:mm:ss a")}
                                </div>
                                <div className="col-md-2">{user.name}: </div>
                                <div className="col-md-2">{text}</div>
                            </div>
                        ))}
                    </div>
                    <form onSubmit={submit} id="form">
                        <div className="input-group">
                            <input
                                type="text"
                                className="form-control"
                                onChange={e => setMessage(e.currentTarget.value)}
                                value={message}
                                id="text"
                            />
                            <span className="btn">
                <button id="submit" type="submit" className="btn btn-success">
                  Envoyer le message
                </button>
              </span>
                        </div>
                    </form>
                </div>
                <div className="col-md-4">
                    <h6>Users</h6>
                    <ul id="users">
                        {users.map(({ name, id }) => (
                            <li key={id}>{name} - <Router><Link to="/messageTo/:name" >Message priv√©</Link></Router></li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    );
};

ReactDOM.render(<App />, document.getElementById("root"));

